//Scanner - hasNextInt()사용
//BufferedReader- null을 반환할 때 종료
import java.util.*;
public class p14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		
		while(sc.hasNextInt()) {
			
			int n1 = sc.nextInt();
			int n2 = sc.nextInt();
			
			System.out.println(n1+n2);
			
			
		}
		
		
	
	}

}
